												IF(ABS(FLOOR(MOD((Scheduled_Resolve_Time__c - NOW())*1440*6,360) / 6)) < 10, "0", "") +
            /* minutes */
            TEXT(
																ABS(FLOOR(MOD((Scheduled_Resolve_Time__c - NOW())*1440*6,360) / 6))
            )
        )
)
