WebLink.url:
(
{!REQUIRESCRIPT("/support/console/45.0/integration.js")}
function() {

var width;
var height;
var title = "Escalate Email Case";
var recId = '{!Case.Id}';
var Escalated= '{!Case.Escalated__c}';
// To Handle Dialog CSS we are using query
var query = "SELECT Escalated__c FROM Case WHERE Id = '" + recId + "' LIMIT 1";
var caseRec = sforce.connection.query(query);
var stats= caseRec.getArray("records");
if(stats[0].Escalated__c == 'true'){
width = 412;
height = 125;
} else{
width = 500;
height = 220;
}
var id=window.location.href.substring(window.location.href.lastIndexOf('/') + 1);

var box = new SimpleDialog("salesforce" + Math.random(), true);
box.setTitle(title);
box.displayX = true;
box.isMovable = false;
box.createDialog();
box.setWidth(width);
// set your html content here
box.setContentInnerHTML(
'<iframe src="/apex/EscalateEmailCase?Id='+recId+'" style="border:none;" width="100%" height="'+ (height - 30)+'px" />'
);
//set the height of the modal
box.dialog.children[1].style.height = height + 'px';
box.show();

//if displayX is set to true, then override standard close event by this code
box.dialog.getElementsByClassName('dialogClose')[0].onclick = function() {
box.hide();
// you can add code to reload the page or redirect to another page
};
//optional : add this to close the modal onclick of the overlay background
box.background.addEventListener('click', function() {
box.hide();
});

// if you want to be able to close the modal from the Visualforce page
window.addEventListener('message', function(event){
var str = event.data;
var res = str.split(" ");
if(event.data === 'close'){
box.hide();
}
else if(res[0] === 'close'){
if(sforce.console.isInConsole()){
let urlrecord = window.location.origin+'/'+res[1]+'/e?retURL='+res[1];
sforce.console.openPrimaryTab(null, urlrecord ,true);
}
else{
window.open(window.location.href ='/'+res[1]+'/e?retURL='+res[1],"_self");
}
box.hide();

}
});
})();
