WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/29.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/29.0/apex.js")}

var selectedIds = {!GETRECORDIDS($ObjectType.Lead)};
if(!selectedIds.length){
	alert('Please select at least one record!');
}else{
	if(confirm("The selected " + selectedIds.length + " records will be processed by Round Robin.")){
		var res = sforce.apex.execute("Janati_RR.AssignmentControllerMultiple","assignLeads_v2",{leadIds: selectedIds });
        if(res && res.length){
            if(res.length === 1 && res[0].indexOf("ERROR" === 0)){
                alert(res[0]);
            }else{
                var moreThanFive = res.length - 5;
                if(moreThanFive > 0){
                    res.length = 5;
                }
                alert('You must have Write access to all leads selected.  No write access for leads for:  ' + res.join(', ') + (moreThanFive > 0 ? (' + ' + moreThanFive + ' more') : ''));
            }
        }else{
          if(ListViewport && ListViewport.instances){ 
            Object.keys(ListViewport.instances).forEach(function(key){ 
             if(ListViewport.instances[key] instanceof ListViewport){ 
               ListViewport.instances[key].refreshList() 
              } 
            }); 
          } 
        }
	}
}
