WebLink.url:
var Celigo = {};
Celigo.SFDC = (function(){ //private members
    function SalesOrderSyncManager(){

        //instance vars
        var that = this;
		var retries = 0;
		var maxRetries = 20;

        //methods
		this.getURLParameter = function(_url, _name){ //String
		    var regexS;
		    var regex;
		    var results;
		    _name = _name.replace(/[\[]/, "\\\[").replace(/[\]]/, "\\\]");
		    regexS = "[\\?&]" + _name + "=([^&#]*)";
		    regex = new RegExp(regexS);
		    results = regex.exec(_url);
		    if (results === null) {
		        return "";
		    }
		    else {
		        return results[1];
		    }
		};

        this.triggerNsSync = function(){

			var o = new sforce.SObject("Opportunity");
			o.id = "{! Opportunity.Id}";
                        o.NetSuite_Quote__c = null;
			o.Celigo_Sync_Helper__c ="false"; 
			o.Send_to_NetSuite__c= "true";
			o.Generate_Sales_Order__c="true";
			o.Celigo_Update__c = "true";
			return sforce.connection.update([o]); 
        };

        this.openPopup = function(){

            var top = screen.height - (screen.height * .5) - 100;
            var left = screen.width - (screen.width * .5) - 187;
            var params = 'dependent = yes,resizable=false,scrollbars=false,toolbar=false,menubar=false,location=false,status=true,directories=false,width=375,height=160,top=';
            params += top.toString();
            params += ',left=' + left.toString() + '\'';

            return window.open('/apex/sales_order_status?whence='+"{!Opportunity.Id}", 'NetSuite_Synchronization', params);
        };

        this.checkSyncStatus = function(){
            var result = sforce.connection.query("select Id, Celigo_Sync_Helper__c, Send_to_NetSuite__c, NS_Sync__c from Opportunity where Id = '"+ "{!Opportunity.Id}" +"'", {
                onSuccess: function(result){
                    var records = result.getArray("records");
                    if (records[0].Celigo_Sync_Helper__c === 'true' && records[0].Send_to_NetSuite__c === 'false' && records[0].NS_Sync__c === null) 
						window.location.href = window.location.href;
					else if(retries < maxRetries){
						retries++;
						window.setTimeout(that.checkSyncStatus, 5000);
					}
                },
                onFailure: function(error){
                    alert('An error has occurred, please contact your Celigo representative: ' + error);
                }
            });
        };

    }
    return { //public members
        main: function(){
            try {
                {!REQUIRESCRIPT("/soap/ajax/13.0/connection.js")}
var inProg= sforce.connection.query("select Id, Send_to_NetSuite__c from Opportunity where Id = '"+ "{!Opportunity.Id}" + "'");
                        var records = inProg.getArray('records');
                        if (records[0].Send_to_NetSuite__c === 'true') 
                            alert("Sync already in progress.");
else {
                var sosm = new SalesOrderSyncManager();
                sosm.triggerNsSync();
                sosm.openPopup();
                window.setTimeout(sosm.checkSyncStatus, 5000);
            } 
}
            catch (e) {
                alert('An error has occurred, please contact your Celigo representative: ' + e.name + ' - ' + e.message);
            }
        }
    }
})();

Celigo.SFDC.main();
