WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/29.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/29.0/apex.js")}

var userProfile = "{!$Profile.Id}";
var opptyStage = "{!Opportunity.StageName}";

var query = "SELECT Count(Id) FROM OpportunityLineItem WHERE OpportunityId = '{!Opportunity.Id}'";
var result = sforce.connection.query(query);
var records = result.getArray("records");
var opptyLineItemCount = records[0].expr0;

function validateBeforeiContract(){
    if (opptyLineItemCount == 0 || opptyStage == "Awareness"){
        alert("Please ensure that the Opportunity has products and Stage is past Awareness before using iContract");
    } else {
        window.open("https://{!$Setup.iContract_Custom_Settings__c.iContract_URL__c}/index.html?opp={!Opportunity.Id}","_blank");
    }
}

validateBeforeiContract()
