WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/13.0/connection.js")}

var records = {!GETRECORDIDS($ObjectType.Request_for_Change__c)};
var newRecords = [];
if (records.length<1)
{
alert("Please select at least one RFC")
}
else
{
for (var n=0; n<records.length; n++)
{
var c = new sforce.SObject("Request_For_Change__c");
c.id = records[n];
c.Assigned_To__c = '{!$User.Id}';
newRecords.push(c);
}
result = sforce.connection.update(newRecords);
window.location.reload();
}
