ValidationRule.errorConditionFormula:
/* DO NOT ever add the override validation rule*/
OR( 
	AND( 
		OR( ISCHANGED(FirstName), ISCHANGED(LastName) ),
		OR( 
			AND( UPPER( PRIORVALUE(FirstName) ) = "PGI", UPPER( PRIORVALUE(LastName) ) = "INTEGRATION" ), 
			AND( UPPER(FirstName) = "PGI",	UPPER(LastName) = "INTEGRATION" )
		) 
	), 
	AND( 
		ISNEW() , 
		AND( UPPER(FirstName) = "PGI",	UPPER(LastName) = "INTEGRATION" )
	) ,
AND(
                    ISCHANGED(Username),
                    LOWER(PRIORVALUE(Username)) = "pgiintegration@pgi.com"
)
)
